import { normolize } from "../../utils/normolize";
import { selectUserIds } from "../selectors";
import { loadUsers, userSlice } from "..";

export const loadUsersIfNotExist = (dispatch, getState) => {

  if (selectUserIds(getState())?.length > 0) {
    return;
  }

  dispatch(userSlice.actions.startLoading());

  fetch("http://localhost:3001/api/users/")
    .then((response) => response.json())
    .then((users) => {
      console.log(users);
      store.dispatch(userSlice.actions.successLoading(normolize(users)));
    })
    .catch(() => {
      console.log("error");
      store.dispatch(userSlice.actions.failLoading());
    });
};
