import { USER_ACTIONS } from "./actions";
import { LoadingStatuses } from "../constants/loadingStatuses";

const initialState = {
  entities: {},
  ids: [],
  status: LoadingStatuses.idle, // 'idle' | 'inProgress' | 'success' | 'failed'
};

export const userReducer = (state = initialState, action) => {
  switch (action.type) {
    case USER_ACTIONS.successLoading: {
      return {
        entities: action.payload.entities,
        ids: action.payload.ids,
        status: LoadingStatuses.success,
      };
    }
    case USER_ACTIONS.failLoading: {
      return {
        entities: {},
        ids: [],
        status: LoadingStatuses.failed,
      };
    }
    case USER_ACTIONS.startLoading: {
      return {
        entities: {},
        ids: [],
        status: LoadingStatuses.inProgress,
      };
    }
    default:
      return state;
  }
};
