import { LoadingStatuses } from "../constants/loadingStatuses";

export const selectUserModule = (state) => state.user;

export const selectUserById = (state, { userId }) =>
  selectUserModule(state).entities[userId];

export const selectUsersIds = (state) => 
  selectUserModule(state).ids;

export const selectUserNameById = (state, { userId }) =>
  selectRestaurantById(state, { restaurantId })?.name;

export const selectUserLoadingStatus = (state) => 
  selectUserModule(state).status;

export const selectUserIsLoading = (state) =>
  selectUserModule(state).status === LoadingStatuses.inProgress;
