import React, { useMemo } from "react";
import { Menu } from "../Menu/Menu";
import { Reviews } from "../Reviews/Reviews";
import { Rating } from "../Rating/Rating";
import { SIZES } from "../../constants/ui";
import { calculateRestaurantRating } from "./helpers";
import { useSelector } from "react-redux";
import {
  selectRestaurantById,
  selectRestaurantNameById,
} from "../../store/restaurant/selectors";

export const Restaurant = ({ restaurantId }) => {
  const restaurantName = useSelector((state) =>
    selectRestaurantNameById(state, { restaurantId })
  );

  // const rating = useMemo(() => calculateRestaurantRating(reviews), [reviews]);

  if (!restaurantName) {
    return <span>NotFound</span>;
  }

  return (
    <div>
      <h1>{restaurantName}</h1>
      {/*<Rating value={rating} size={SIZES.l} />*/}
      <Menu restaurantId={restaurantId} />
      <Reviews restaurantId={restaurantId} />
    </div>
  );
};
