import { Restaurant } from "../../components/Restaurant/Restaurant";
import { useState } from "react";

import styles from "./styles.module.css";
import { useSelector } from "react-redux";
import { selectRestaurantsIds } from "../../store/restaurant/selectors";
import { selectReviewIds } from "../../store/review/selectors";
import { RestaurantTabs } from "../../components/RestaurantTabs/RestaurantTabs";
import { Cart } from "../../components/Cart/Cart";

export const RestaurantsPage = () => {
  const restaurantIds = useSelector(selectRestaurantsIds);
  const reviewsIds = useSelector(selectReviewIds);
  let [activeRestaurantId, setActiveRestaurantId] = useState(restaurantIds[0]);

  return (
    <div className={styles.root}>
      <RestaurantTabs
        activeRestaurantId={activeRestaurantId}
        onRestaurantSelect={setActiveRestaurantId}
      />
      {activeRestaurantId !== undefined && (
        <Restaurant restaurantId={activeRestaurantId} />
      )}
      <Cart />
    </div>
  );
};
