import {React, useState} from "react";


export const Dish = ({ name }) => {
  let [count, setCount] = useState(0);

  return (
    <div>
      <button onClick = {() => setCount(++count) }>
        +
      </button>

      <span>
        {count}
      </span>

      <button 
        onClick = {() => count ? setCount(--count) : count }>
        -
      </button>

      <span>{name}</span>
    </div>
  )
};
