import React from "react";
import { useSelector } from "react-redux";
import { NavLink } from "react-router-dom";
import { selectRestaurantNameById } from "../../store/restaurant/selectors";
import { useParams } from "react-router-dom";
import { Outlet } from "react-router-dom";
import  styles  from "./styles.module.css";
import classNames from "classnames";


export const Restaurant = () => {
  const { restaurantId } = useParams();

  const restaurantName = useSelector((state) =>
    selectRestaurantNameById(state, { restaurantId })
  );

  // const rating = useMemo(() => calculateRestaurantRating(reviews), [reviews]);

  if (!restaurantName) {
    return <span>NotFound</span>;
  }

  return (
    <div>
      <h1>{restaurantName}</h1>
      <div>
        <NavLink
             to="menu"
             className={({ isActive }) =>
             isActive ?  classNames(styles.isActive)  : undefined
           }
        > 
          <button>Menu btn</button>
        </NavLink>
        <NavLink 
          to="review"
          className={({ isActive }) =>
             isActive ? classNames(styles.isActive) : undefined
           }
        >   
          <button>Review btn</button>
        </NavLink>
      </div>
      <div>
        <Outlet/>
      </div>
    </div>
  );
};
