import { normolize } from "../../utils/normolize";
import { selectReviewsIds } from "../selectors";
import { selectReviewsByRestaurantId } from "../../restaurant/selectors";
import { loadReviews, reviewSlice } from "..";

export const loadReviewsIfNotExist = (restaurantId) => (dispatch, getState) => {
  if (action.type !== loadReviews.type) {
    next(action);
    return;
  }

  const restaurantReviewIds = selectReviewsByRestaurantId(store.getState(), {
    restaurantId: action.payload,
  });
  const loadedReviewIds = selectReviewsIds(store.getState()) || [];

  if (
    restaurantReviewIds?.every((restaurantReviewId) =>
      loadedReviewIds.includes(restaurantReviewId)
    )
  ) {
    return;
  }

  store.dispatch(reviewSlice.actions.startLoading());

  fetch(`http://localhost:3001/api/reviews?id=${action.payload}`)
    .then((response) => response.json())
    .then((reviews) => {
      console.log(reviews);
      store.dispatch(reviewSlice.actions.successLoading(normolize(reviews)));
    })
    .catch(() => {
      console.log("error");
      store.dispatch(reviewSlice.actions.failLoading());
    });
};
