import React from "react";
import { Review } from "../Review/Review";

export const Reviews = ({ reviews }) => {
  if (!reviews?.length) {
    return <span>Пусто тут</span>;
  }

  return (
    <div>
      <h2>Reviews</h2>
      <div>
        {reviews.map(({ text }, index) => (
          <Review key={index} text={text} />
        ))}
      </div>
    </div>
  );
};
