import styles from "./styles.module.css";
import classnames from "classnames";
import { useReducer } from "react";
import { Rating } from "../Rating/Rating";


const INITIAL_FORM_VALUE = {
  name: {value: "", touched: false, hasError: false, error: ""},
  text: "",
  email: {value: "", hasError: false, error: ""},
  rating: 1,
};

const ACTIONS = {
  setName: "setName",
  setText: "setText",
  setEmail: "setEmail",
  setRating: "setRating",
  clear: "clear",
};

const formReducer = (state, action) => {
  switch(action.type) {
    case ACTIONS.setName:  {
      const { hasError, error } = validation.maxLength(action.payload);
      return {...state, name: { value: action.payload, touched: true, hasError: hasError, error: error }}
    }

    case ACTIONS.setText: {
      return {...state, text: action.payload }
    }

    default: 
      return state;
  }
};

const validation = {

  isEmpty: (value) => {
    if(value.length < 1) {
      return {hasError: true, error: "Requared field"}
    }
    return { hasError: false, error: "" }
  },
  maxLength: (value) => {
    if (value.length > 5) {
      return { hasError: true, error: "Uncorrect string length: max length 5"}
    }  
    return { hasError: false, error: ""}
  }

}

const addValidationStyles = (hasError, touched) => {
  if (hasError) {
    return styles.error;
  }

  if(touched) {
    return styles.succes;
  }
}

export const NewReviewForm = ({ className }) => {
  const [formValue, dispatch] = useReducer(formReducer, INITIAL_FORM_VALUE);

  const onNameChange = (event) => {
    dispatch({ type: ACTIONS.setName, payload: event.target.value });
  };
  const onTextChange = (event) => {
    dispatch({ type: ACTIONS.setText, payload: event.target.value });
  };
  // const onEmailChange = (event) => {
  //   dispatch({ type: ACTIONS.setEmail, payload: event.target.value });
  // };
  // const onRatingChange = (value) => {
  //   dispatch({ type: ACTIONS.setRating, payload: value });
  // };

  return (
    <div className={classnames(styles.root, className)}>
      <div className={classnames(styles.formControl, addValidationStyles(formValue.name.hasError, formValue.name.touched))}>
        <label htmlFor="name">Name</label>
        <input
          name="name"
          type="text"
          value={formValue.name.value}
          onChange={onNameChange}
        />
      </div>
      <div className={styles.formControl}>
        <label htmlFor="text">Text</label>
        <input
          name="text"
          type="text"
          className={styles.input}
          value={formValue.text}
          onChange={onTextChange}
        />
      </div>
      {/* 
      <div className={styles.formControl}>
        <label htmlFor="text">Email</label>
        <input
          name="text"
          type="email"
          className={styles.input}
          value={formValue?.email?.value}
          onChange={onEmailChange}
        />
      </div> */}
      <div className={styles.formControl}>
        <label>Choose rating</label>
        {/* <Rating value={formValue.rating} onChange={onRatingChange} /> */}
      </div>
      <button className={styles.clear} onClick={() => dispatch({ type: ACTIONS.clear })}>Clear</button>
    </div>
  );
};
