import { LoadingStatuses } from "../constants/loadingStatuses";
import { REVIEWS_ACTIONS } from "./actions";

const initialState = {
  entities: {},
  ids: [],
  status: LoadingStatuses.idle
};

export const reviewReducer = (state = initialState, action) => {
  switch (action.type) {
    case REVIEWS_ACTIONS.startLoading: {
      return {
        ...state,
        status: LoadingStatuses.inProgress,
      }
    }

    case REVIEWS_ACTIONS.successLoading: {
      return {
        entities: {
          ...state.entities, 
          ...action.payload.entities
        },
        ids: Array.from(new Set([...state.ids, ...action.payload.ids])),
        status: LoadingStatuses.success
      }
    }

    case REVIEWS_ACTIONS.failLoading: {
      return {
        ...state,
        status: LoadingStatuses.failed
      }
    }

    default:
      return state;
  }
};
