import { LoadingStatuses } from "../constants/loadingStatuses";

export const selectReviewModule = (state) => state.review;

export const selectReviewById = (state, { reviewId }) =>
  selectReviewModule(state).entities[reviewId];

export const selectReviewsIds = (state) => selectReviewModule(state).ids;

export const selectReviewsStatus = (state) => selectReviewModule(state).status;

export const selectReviewStatusLoading = (state) => 
selectReviewModule(state).status === LoadingStatuses.inProgress;
