import React, { useEffect } from "react";
import { Review } from "../Review/Review";
import { NewReviewForm } from "../NewReviewForm/NewReviewForm";

import styles from "./styles.module.css";
import { useDispatch, useSelector } from "react-redux";
import { selectReviewsByRestaurantId } from "../../store/restaurant/selectors";
import { selectReviewStatusLoading } from "../../store/review/selectors";
import { loadReview } from "../../store/review/actions";

export const Reviews = ({ restaurantId }) => {
  const reviewIds = useSelector((state) =>
    selectReviewsByRestaurantId(state, { restaurantId })
  );
  const dispatch = useDispatch();
  const isLoading = useSelector(selectReviewStatusLoading);

  useEffect(()=> {
    dispatch(loadReview(restaurantId));
  }, [restaurantId]);

  if(isLoading)

  if (!reviewIds?.length) {
    return <span>Пусто тут</span>;
  }

  return (
    <div className={styles.root}>
      <h2>Reviews</h2>
      <div className={styles.reviews}>
        {reviewIds.map((id) => (
          <Review key={id} reviewId={id} />
        ))}
      </div>
      <NewReviewForm />
    </div>
  );
};
