import {React, useState} from "react";

export const Dish = ({ name }) => {
  let [count, setCount] = useState(0);

  function changeCount(toIncrease) {
      if (toIncrease) setCount(count + 1);
      if (!toIncrease && count > 0) setCount(count - 1);
  }

  return (
      <div style={{ display: 'flex', marginLeft: '10px', marginBottom: '7px' }}>
          {name}
          <button onClick={() => changeCount(false)}>-</button>
          {count}
          <button onClick={() => changeCount(true)}>+</button>
      </div >
  )
};
