import { cartReducer } from "./cart/reducer";
import { applyMiddleware, combineReducers, createStore } from "redux";
import { restaurantReducer } from "./restaurant/reducer";
import { dishReducer } from "./dish/reducer";
import { reviewReducer } from "./review/reducer";
import { userReducer } from "./user/reducer";
import { loggerMiddleware } from "./middleware/logger";
import { loadRestaurantsIfNotExist } from "./restaurant/middlewares/loadRestaurantsIfNotExist";
import { loadDishesIfNotExist } from "./dish/middlewares/loadDishesIfNotExist";
import { loadUsersIfNotExist } from "./user/middlewares/loadUsersIfNotExist";
import { loadReviewsIfNotExist } from "./review/middleware/loadReviewsIfNotExist";

const rootReducer = combineReducers({
  cart: cartReducer,
  restaurant: restaurantReducer,
  dish: dishReducer,
  review: reviewReducer,
  user: userReducer,
});

export const store = createStore(
  rootReducer,
  applyMiddleware(
    loggerMiddleware,
    loadRestaurantsIfNotExist,
    loadDishesIfNotExist,
    loadUsersIfNotExist,
    loadReviewsIfNotExist
  )
);

// store
// dispatch ==action==> middleware ==action==> reducer(action)
