import styles from "./styles.module.css";
import { Rating } from "../Rating/Rating";
import { useDispatch, useSelector } from "react-redux";
import { selectReviewById } from "../../store/review/selectors";
import { User } from "../User/User";
import { selectUserIsLoading, selectUsersIds } from "../../store/user/selectors";
import { useEffect } from "react";
import { loadUsers } from "../../store/user/actions";

export const Review = ({ reviewId }) => {
  const dispatch = useDispatch();
  const userIds = useSelector(selectUsersIds);
  const isLoading = useSelector(selectUserIsLoading);
  const review = useSelector((state) => selectReviewById(state, { reviewId }));

  useEffect(()=> { 
    dispatch(loadUsers());
  }, []);

  if (isLoading) {
    console.log();
    return <span>Loading.....</span>;
  }

  if (!userIds?.length) {
    return <span>Empty</span>;
  }

  if(!review) {
    return <span>review is empty</span>
  }

  return (
    <div className={styles.root}>
      <div>{review.text}</div>
      <Rating value={review.rating} size="s" />
      <User userId={review.userId} />
    </div>
  );
};
