import {
  failLoading,
  USER_ACTIONS,
} from "../actions";
import { normolize } from "../../utils/normolize";
import { selectUserIds } from "../selectors";
import { userSlice } from "..";

export const loadUsersIfNotExist = (store) => (next) => (action) => {
  if (action.type !== USER_ACTIONS.loadUsers) {
    next(action);
    return;
  }

  if (selectUserIds(store.getState())?.length > 0) {
    return;
  }

  store.dispatch(startLoading());

  fetch("http://localhost:3001/api/users/")
    .then((response) => response.json())
    .then((users) => {
      console.log(users);
      store.dispatch(userSlice.actions.successLoading(normolize(users)));
    })
    .catch(() => {
      console.log("error");
      store.dispatch(userSlice.actions.failLoading());
    });
};
