import classnames from "classnames";

import styles from "./styles.module.css";
import { Button } from "../Button/Button";
// import { Ingredient } from "../Ingredient/Ingredient";
import { useCount } from "../../hooks/useCount";
import { useDispatch } from "../../CustomStore/hooks/useDispatch";
import { useSelector } from "../../CustomStore/hooks/useSelector";
import { removeDish } from "../../pages/store/cart/action";

export const Dish = ({ name, ingredients, className }) => {
  // const { count, increment, decrement } = useCount();
  const dispatch = useDispatch();
  const count = useSelector(state => state.cart?.[name] || 0);

  const decrement = () => dispatch(removeDish(name))

  return (
    <div className={classnames(styles.root, className)}>
      <div className={styles.dish}>
        <div
          className={classnames(styles.dishName, {
            [styles.max]: count > 5,
            [styles.min]: count < 2,
            [styles.norm]: count >= 2 && count <= 5,
          })}
        >
          {name}
        </div>
        <div>
          {/* <Button onClick={decrement}>-</Button>
          {count}
          <Button onClick={increment}>+</Button> */}
          {count}
        </div>
      </div>
    </div>
  );
};
