import {React, useState} from "react";


export const Dish = ({ name, updated }) => {
  let [count, setCount] = useState(0);

  console.log(``)

  return (
    <div>
      <button 
        onClick = {() => {
          setCount(++count);
          console.log(`Click ${name} btn+ ${count}`);
          }
        }
      >
        +
      </button>

      <span>
        {count}
      </span>

      <button 
        onClick = {() => {
          if(count) {
            setCount(--count)
          }
          console.log(`Click ${name} btn- ${count}`);
        }
      }
      >
        -
      </button>

      <span>{name}</span>
    </div>
  )
};
