import { normalizedProducts } from "../../constants/normalized-fixtures";
import { DISH_ACTIONS } from "./actions";
import { LoadingStatuses } from "../constants/loadingStatuses";

const initialState = {
  entities: {},
  ids: [],
  status: LoadingStatuses.idle,
};

export const dishReducer = (state = initialState, action) => {
  switch (action.type) {
    case DISH_ACTIONS.startLoading: {
      return {
        ...state,
        status: LoadingStatuses.inProgress,
      };
    }
    case DISH_ACTIONS.successLoading: {
      return {
        entities: {
          ...state.entities,
          ...action.payload.entities,
        },
        ids: Array.from(new Set([...state.ids, ...action.payload.ids])),
        status: LoadingStatuses.success,
      };
    }
    case DISH_ACTIONS.failLoading: {
      return {
        ...state,
        status: LoadingStatuses.failed,
      };
    }
    default:
      return state;
  }
};
