import { CART_ACTIONS } from "./action";

const initialState = {};

export const CartReducer = (state = initialState, action) => {
  switch(action.type) {
    case CART_ACTIONS.addDish: {
      return {
        ...state,
        [action.payload]: (state[action.payload] || 0) + 1,
      }
    }
    case CART_ACTIONS.removeDish: {
      return {
        ...state,
        [action.payload]: state[action.payload] ? state[action.payload] - 1 : 0,
      }
    }
    case CART_ACTIONS.clearCart: {
      return { };
    }
    default: 
      return state;
  }
}
