import { LoadingStatuses } from "../../constants/loadingStatuses"
import { restaurantReducer } from "../../restaurant/reducer";
import { normolize } from "../../utils/normolize";
import { failLoading, startLoading, successLoading, USER_ACTIONS } from "../actions"
import { selectUsersIds } from "../selectors";

export const loadUsersIfNotExist = (store) => (next) => (action) => {
  if(action.type !== USER_ACTIONS.loadUsers ) {
    next(action);
    return;
  }

  if(selectUsersIds( store.getState())?.length > 0) {
    return;
  }

  store.dispatch(startLoading());

  fetch('http://localhost:3001/api/users')
    .then((response) => response.json())
    .then ((restaurants) => {
      console.log
      store.dispatch(successLoading(normolize(restaurants)))
    })
    .catch(()=> {
      console.log('User Error');
      store.dispatch(failLoading());
    });

}
