import { USER_ACTIONS } from "./actions";

export const selectUserModule = (state) => state.user;

export const selectUserById = (state, { userId }) =>
  selectUserModule(state).entities[userId];

export const selectUsersIds = (state) => selectUserModule(state).ids;

export const selectUserStatus = (state) => selectUserModule(state).status;

export const selectUserIsLoading = (state) => selectUserModule(state).status === USER_ACTIONS;
