import { failLoading } from "../../dish/actions";
import { selectReviewsByRestaurantId } from "../../restaurant/selectors";
import { normolize } from "../../utils/normolize";
import { REVIEWS_ACTIONS, startLoading, successLoading } from "../actions"

export const loadReviewsIfNotExist = (store) => (next) => (action) => {
  if(action.type !== REVIEWS_ACTIONS.loadReview) {
    next(action);
    return;
  }

  const restaurantDishIds = selectMenuByRestaurantId(store.getState(), {
    restaurantId: action.payload,
  });
  const loadedDishIds = selectDishsIds(store.getState()) || [];

  if (
    restaurantDishIds?.every((restaurantDishId) =>
      loadedDishIds.includes(restaurantDishId)
    )
  ) {
    return;
  }


  store.dispatch(startLoading());

  fetch(`http://localhost:3001/api/reviews?id=${action.payload}`)
    .then((response) => response.json())
    .then ((reviews) => {
        console.log(reviews, 'REVIEWS');
        store.dispatch(successLoading(normolize(reviews)))
    })
    .catch(()=> {
      console.log("error REVIEWS");
      store.dispatch(failLoading());
    }) 
}
